import React from "react";
import classes from '../css/select.css';
const select = ({list, getItem}) =>{
    return(
        <select className={classes.select} onChange={(e)=> getItem(e.target.value)}>
            {list.map(opt=>
                <option key={opt}>{opt}</option>
            )}
        </select> 
    )
}
export default select;
